import React from "react";
import { View, Text, TouchableOpacity, Image, FlatList, Switch } from "react-native";
import tw from "twrnc";
import { useState } from "react";
import Ionicons from "react-native-vector-icons/Ionicons";


const produtos = [
    {
      id: "1",
      nome: "Tênis Esportivo",
      preco: 199.99,
      imagem: "data:image/jpeg;base64,", 
      descricao: "Tênis confortável para corridas e esportes.",
    },
    {
      id: "2",
      nome: "Camiseta Casual",
      preco: 49.90,
      imagem: "data:image/jpeg;base64,", 
      descricao: "Camiseta de algodão para o dia a dia.",
    },
    {
      id: "3",
      nome: "Mochila Moderna",
      preco: 89.90,
      imagem: "data:image/jpeg;base64,", 
      descricao: "Mochila resistente com vários compartimentos.",
    },
    {
      id: "4",
      nome: "Relógio Digital",
      preco: 149.50,
      imagem: "data:image/jpeg;base64,", 
      descricao: "Relógio elegante com funções digitais.",
    },
    {
      id: "5",
      nome: "Calça Jeans",
      preco: 129.99,
      imagem: "data:image/jpeg;base64,", 
      descricao: "Calça jeans slim fit de alta qualidade.",
    },
  ];
  export default function ProductListScreen({ navigation }) {
    const [isDarkMode, setIsDarkMode] = useState(false);
  
    const toggleTheme = () => {
      setIsDarkMode((prevMode) => !prevMode);
    };
  
    const containerStyle = isDarkMode
    ? tw`flex-1 bg-black mt-`
    : tw`flex-1 bg-gray-100 mt-4`;
  const cardStyle = isDarkMode
    ? tw`flex-row items-center p-4 bg-gray-900 rounded-lg shadow-md mb-2 mx-2`
    : tw`flex-row items-center p-4 bg-white rounded-lg shadow-md mb-2 mx-2`;
  const titleStyle = isDarkMode
    ? tw`text-lg font-bold text-gray-100`
    : tw`text-lg font-bold text-gray-800`;
  const priceStyle = isDarkMode
    ? tw`text-sm text-gray-300`
    : tw`text-sm text-gray-600`;
  
    const renderItem = ({ item }) => (
        <TouchableOpacity
          style={cardStyle}
          onPress={() => navigation.navigate("ProductDetail", { produto: item, isDarkMode })} //route.params que envia os dados daqui pra lá
        >
          <Image
            source={{ uri: item.imagem }} //colet dados das constantes acima e trás pra cá
            style={tw`w-16 h-16 rounded-md mr-4`}
          />
          <View style={tw`flex-1`}> 
            <Text style={titleStyle}>{item.nome}</Text>
            <Text style={priceStyle}>R$ {item.preco.toFixed(2)}</Text>
          </View>
        </TouchableOpacity>
      );    
  
    return (
      <View style={containerStyle}>
        <View style={tw`p-4 flex-row justify-end`}>
          
        </View>
  
        <FlatList
          data={produtos}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={tw`pb-4`}
        />
        <Ionicons
          onPress={toggleTheme}
          style={tw`mb-16 ml-80 `}
            name={isDarkMode ? "sunny" : "moon"}
            size={40}
            color={isDarkMode ? "#ffffff" : "#000000"}
          />
      </View>
    );
  }